// –¢–∏–ø—ã –Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏—è —Ä–∞–∑—Ä—ã–≤–∞
type GapDirection = 'personal_higher' | 'relationship_higher' | 'balanced';

// –¢–∏–ø—ã —Å–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç–∏
type CompatibilityType = 'highly_compatible' | 'moderately_compatible' | 'challenging' | 'critical';

// –ü–æ–ª—É—á–µ–Ω–∏–µ –Ω–∞–ø—Ä–∞–≤–ª–µ–Ω–∏—è —Ä–∞–∑—Ä—ã–≤–∞
function getGapDirection(personal: number, relationship: number): GapDirection {
  const diff = personal - relationship;
  if (Math.abs(diff) <= 1) return 'balanced';
  return diff > 0 ? 'personal_higher' : 'relationship_higher';
}

// –ü–æ–ª—É—á–µ–Ω–∏–µ —Ç–∏–ø–∞ —Å–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç–∏
function getCompatibilityType(gap: number): CompatibilityType {
  if (gap <= 1) return 'highly_compatible';
  if (gap <= 3) return 'moderately_compatible';
  if (gap <= 5) return 'challenging';
  return 'critical';
}

